INSERT INTO trip2world.site (continent, country, city, site_name, description, latitude, longitude, open_time, season, ticket, free, link) VALUES
        ('亚洲', '中国', '上海市','上海迪士尼乐园','上海迪士尼乐园，位于上海市浦东新区川沙镇黄赵路310号，于2016年6月16日正式开园，是中国内地首座迪士尼主题乐园，也是中国规模最大的现代服务业中外合作项目之一，是一座具有纯正迪士尼风格并融汇了中国风的主题乐园。上海迪士尼乐园占地1.16平方公里，主题园区分为米奇大街、奇想花园、探险岛、宝藏湾、明日世界、梦幻世界、迪士尼·皮克斯玩具总动员。拥有迪士尼城堡、漫威英雄总部、巴斯光年星际营救等游乐项目。',121.40534,31.211329,'8:30-21:30','春季',1642340,0,'https://www.bilibili.com/video/BV1op4y1b7hf?p=1&vd_source=df79d8622b20f745687871ec7a054788'),
        ('亚洲', '中国', '北京市','故宫','故宫位于北京市中心，旧称紫禁城。于明代永乐十八年（1420年）建成，是明、清两代的皇宫，无与伦比的古代建筑杰作，世界现存最大、最完整的木质结构的古建筑群。故宫全部建筑由“前朝”与“内廷”两部分组成，四周有城墙围绕。四面由筒子河环抱。城四角有角楼。四面各有一门，正南是午门，为故宫的正门。这里曾居住过24个皇帝，是明清两朝（公元1368～1912年）的皇宫，无与伦比的古代建筑杰作，世界现存最大、最完整的木质结构的古建筑群。',116.397026,39.918058,'8:30-16:30','春季',5124832,0,'https://www.bilibili.com/video/BV1jL4y1V7AM/?spm_id_from=333.337.search-card.all.click&vd_source=df79d8622b20f745687871ec7a054788'),
        ('亚洲', '中国', '杭州市','西湖','西湖傍杭州而盛，杭州因西湖而名。自古以来，“天下西湖三十六，就中最美是杭州”，以西湖为中心的西湖景区，是国务院首批公布的国家重点风景名胜区，也是全国首批十大文明风景旅游区和国家AAAAA级旅游景区。她三面云山，中涵碧水，面积约60平方公里，其中湖面6.5平方公里。',120.108478,30.220671,'全天','夏季',0,1,'https://www.bilibili.com/video/BV1Zt411G7sa/?spm_id_from=333.337.search-card.all.click&vd_source=df79d8622b20f745687871ec7a054788'),
        ('亚洲', '中国', '拉萨市','布达拉宫','布达拉宫（藏文：པོ་ཏ་ལ），坐落于中国西藏自治区的首府拉萨市区西北玛布日山上，是世界上海拔最高，集宫殿、城堡和寺院于一体的宏伟建筑，也是西藏最庞大、最完整的古代宫堡建筑群。布达拉宫依山垒砌，群楼重叠，是藏式古建筑的杰出代表（据说源于桑珠孜宗堡），中华民族古建筑的精华之作，是第五套人民币50元纸币背面的风景图案 。主体建筑分为白宫和红宫两部分。宫殿高200余米，外观13层，内为9层。布达拉宫前辟有布达拉宫广场，是世界上海拔最高的城市广场。布达拉宫是西藏的象征，拉萨的著名景点和标志性建筑。作为海拔最高的宫殿，举世闻名。',91.118473,29.654494,'9:30—15：20','夏季',1398724,0,'https://www.bilibili.com/video/BV1CJ411z72i/?spm_id_from=333.337.search-card.all.click&vd_source=df79d8622b20f745687871ec7a054788'),
        ('亚洲', '中国', '西安市','秦始皇陵兵马俑','兵马俑，即秦始皇陵兵马俑，亦简称秦兵马俑或秦俑，是第一批全国重点文物保护单位、第一批中国世界遗产，位于今陕西省西安市临潼区秦始皇陵以东1.5千米处的兵马俑坑内。先后有200多位外国元首和政府首脑参观访问，成为中国古代辉煌文明的一张金字名片，又被誉为世界十大古墓稀世珍宝之一。兵马俑是古代墓葬雕塑的一个类别。古代施行人殉，奴隶是奴隶主生前的附属品，奴隶主死后奴隶要作为殉葬品为奴隶主陪葬。兵马俑即制成兵马（战车、战马、士兵）形状的殉葬品。',109.28093,34.38375,'8:30-17:00','秋季',1783674,0,'https://www.bilibili.com/video/BV12m4y177oK/?spm_id_from=333.337.search-card.all.click&vd_source=df79d8622b20f745687871ec7a054788'),
        ('亚洲', '中国', '敦煌市','莫高窟','莫高窟，坐落于河西走廊的西部尽头的敦煌。它的开凿从十六国时期至元代，前后延续约1000年，这在中国石窟中绝无仅有。它既是中国古代文明的一个璀璨的艺术宝库，也是古代丝绸之路上曾经发生过的不同文明之间对话和交流的重要见证。莫高窟现有洞窟735个，保存壁画4.5万多平方米，彩塑2400余尊，唐宋木构窟檐5座，是中国石窟艺术发展演变的一个缩影，在石窟艺术中享有崇高的历史地位。窟内绘、塑佛像及佛典内容，为佛徒修行、观像、礼拜处所。敦煌石窟是建筑、雕塑、壁画三者结合的立体艺术。洞窟分南北两区：南区492个洞窟是莫高窟礼佛活动的场所，北区243个洞窟主要是僧人和工匠的居住地，内有修行和生活设施土炕坑、烟道、壁龛、灯台等，但多无彩塑和壁画。',121.505246,30.076361,'07:30-18:00','秋季',1687523,0,'https://www.bilibili.com/video/BV1uv4y1W7Ud/?spm_id_from=333.337.search-card.all.click&vd_source=df79d8622b20f745687871ec7a054788'),
        ('亚洲', '中国', '阿坝藏族羌族自治州','九寨沟','九寨沟位于四川省西北部岷山山脉南段的阿坝藏族羌族自治州九寨沟县漳扎镇境内，地处岷山南段弓杠岭的东北侧。距离成都市400多千米，系长江水系嘉陵江上游白水江源头的一条大支沟。 九寨沟自然保护区地势南高北低，山谷深切，高差悬殊。北缘九寨沟口海拔仅2000米，中部峰岭均在4000米以上，南缘达4500米以上，主沟长30多公里。九寨沟是世界自然遗产、国家重点风景名胜区、国家AAAAA级旅游景区、国家级自然保护区、国家地质公园、世界生物圈保护区网络，也是中国第一个以保护自然风景为主要目的的自然保护区。',104.243841,33.252056,'7:30-14:00','秋季',2034324,0,'https://www.bilibili.com/video/BV1gm4y1h7eD/?spm_id_from=333.337.search-card.all.click&vd_source=df79d8622b20f745687871ec7a054788'),
        ('亚洲', '中国', '北京市','八达岭长城','八达岭长城位于北京市延庆区军都山关沟古道北口，史称天下九塞之一，是长城重要关口居庸关的前哨，在明长城中保存最好，也最具代表性，因此是万里长城向游人开放最早的地段。 八达岭长城其关城为东窄西宽的梯形，建于明弘治十八年（1505年），嘉靖、万历年间曾修葺。关城有东西二门，东门额题“居庸外镇”，刻于嘉靖十八年（1539年）；西门额题“北门锁钥”，刻于万历十年（1582年）。两门均为砖石结构，券洞上为平台，台之南北各有通道，连接关城城墙，台上四周砌垛口。 古称“居庸之险不在关而在八达岭”。该段长城地势险峻，居高临下，集巍峨险峻、秀丽苍翠于一体，1953年修复关城和部分城墙后，辟为游览区。经多次整修，可供游览地段达3741米，其中南段1176米、北段2565米，共有敌台16座。',116.016802,40.356188,'6:30-16:30','春季',3928992,0,'https://www.bilibili.com/video/BV1Ze411M7sg/?spm_id_from=333.337.search-card.all.click&vd_source=df79d8622b20f745687871ec7a054788'),
        ('亚洲', '中国', '黄山市','黄山','黄山是世界文化与自然遗产、世界地质公园、世界生物圈保护区，是国家级风景名胜区、全国文明风景旅游区、国家5A级旅游景区，与长江、长城、黄河同为中华壮丽山河和灿烂文化的杰出代表，被世人誉为“人间仙境”、“天下第一奇山”，素以奇松、怪石、云海、温泉、冬雪“五绝”著称于世。境内群峰竞秀，怪石林立，有千米以上高峰88座，“莲花”、“光明顶”、“天都”三大主峰，海拔均逾1800米。明代大旅行家徐霞客曾两次登临黄山，赞叹道：“薄海内外无如徽之黄山，登黄山天下无山，观止矣！”后人据此概括为“五岳归来不看山，黄山归来不看岳”。',118.2014318,30.305406,'7：00-17:00','夏季',932756,0,'https://www.bilibili.com/video/BV1no4y1w7Dc/?spm_id_from=333.337.search-card.all.click&vd_source=df79d8622b20f745687871ec7a054788'),
        ('亚洲', '中国', '西宁市','青海湖','青海湖，地处青海高原的东北部，西宁市的西北部，是我国第一大内陆湖泊，也是我国最大的咸水湖。青海湖面积达4456平方公里，环湖周长360多公里，比著名的太湖大一倍还要多。湖面东西长，南北窄，略呈椭圆形。青海湖水平均深约21米多，最大水深为32.8米，蓄水量达1050亿立方米，湖面海拔为3260米，比两个东岳泰山还要高。',100.185245,36.952865,'8:00-17：00','夏季',678252,0,'https://www.bilibili.com/video/BV1n24y1F7P2/?spm_id_from=333.337.search-card.all.click&vd_source=df79d8622b20f745687871ec7a054788');
